CustomField.formula:
IF(
    NOT(ISBLANK(LeadId)),
    /* Check if Lead is associated */
    IF(
        Lead.Permission__c,
        "True",
        "False"
    ),
    IF(
        NOT(ISBLANK(ContactId)),
        /* Check if Contact is associated */
        IF(
            Contact.Permission__c,
            "True",
            "False"
        ),
        "Blank"
    )
)
